import BoardSquare from "../components/BoardSquare.js"
import Dice from "../components/Dice.js"
import {useState, useRef} from "react";

const unrolledDice = [ ["B", "K", "N", "S", "X", "Z"],
                       ["I", "I", "N", "N", "O", "Y"],
                       ["F", "G", "K", "P", "P", "V"],
                       ["D", "G", "G", "L", "R", "R"],
                       ["C", "C", "M", "S", "T", "T"],
                       ["D", "F", "L", "L", "R", "W"],
                       ["A", "A", "E", "E", "O", "O"],
                       ["A", "E", "I", "O", "U", "U"],
                       ["H", "H", "P", "T", "T", "W"],
                       ["B", "L", "L", "M", "M", "Y"],
                       ["B", "C", "C", "D", "T", "J"],
                       ["H", "H", "N", "N", "R", "R"]
]
function rollDice() {
    let rolledDice = [];
        for (const dice of unrolledDice) {
            let letterIdx = Math.floor(Math.random() * 6);
            let newDice = dice[letterIdx];
            rolledDice.push(newDice)
        }
        return rolledDice;

}

export default function Board() {
    const boardRef = useRef(null);
    let diceArray = rollDice();
    let boardArray = [];
    buildBoard();

    function buildBoard() {
        for (let i = 0; i < 12; i++) {
            for (let j = 0; j < 12; j++) {
                if (i === 0) {
                    boardArray.push(<div class="boardsquare"><div class="dice">{diceArray[j]}</div></div>);
                } else {
                    boardArray.push(<div class="boardsquare"></div>);
                }
            }
        }
    }

    //const [board, setBoard] = useState(initialBoardState);
    //const [activeDice, setActiveDice] = useState(null);

    let activeDice = null;

    function grabPiece(e) {
        const element = e.target;
        const board = boardRef.current;

        if (element.classList.contains("dice") && board) {
            const x = e.clientX - 30;
            const y = e.clientY - 30;

            element.style.position = "absolute";
            element.style.left = `${x}px`;
            element.style.top = `${y}px`;

            activeDice = element

        }
    }

    function movePiece(e) {
        const board = boardRef.current;
        if (activeDice && board) { 

            const minX = board.offsetLeft;
            const minY = board.offsetTop;
            const maxX = board.offsetLeft + board.clientWidth - 45;
            const maxY = board.offsetTop + board.clientHeight - 45;
            const x = e.clientX - 30;
            const y = e.clientY - 30;
            activeDice.style.position = "absolute";

            if (x > maxX) {
                activeDice.style.left = `${maxX - 7}px`;
                //activeDice = null;
            } else if (x < minX) {
                activeDice.style.left = `${minX + 5}px`;
                //activeDice = null;
            } else {
                activeDice.style.left = `${x}px`;
            }

            if (y > maxY) {
                activeDice.style.top = `${maxY - 9}px`;
                //activeDice = null;
            } else if (y < minY) {
                activeDice.style.top = `${minY + 5}px`;
                //activeDice = null;
            } else {
                activeDice.style.top = `${y}px`;
            }

        }
    }

    function dropPiece(e) {
        if (activeDice) {
            const board = boardRef.current;
            const minX = board.offsetLeft;
            const minY = board.offsetTop;
            const x = (Math.floor((e.clientX - minX) / 60) * 60) + minX + 7;
            const y = (Math.floor((e.clientY - minY) / 60) * 60) + minY + 6.5;
            activeDice.style.top = `${y}px`;
            activeDice.style.left = `${x}px`;

            const element = e.target;
            element.parentNode.removeChild(element);
            let currSquare = document.querySelector('.boardsquare');
            currSquare.appendChild(element);
            console.log(currSquare);
            console.log(boardArray);
            //exportBoardArray();

            activeDice = null;
        }
    }

    function exportBoardArray() {
        let boardStateArray = [];
        for (const square of boardArray) {
            if (Object.keys(square.props).includes("children")) {
                boardStateArray.push(square.props.children.props.children);
            } else {
                boardStateArray.push(null);
            }
        }
        console.log(boardStateArray);
    }

    return (<div onMouseDown={e => grabPiece(e)}
                 onMouseMove={e => movePiece(e)}
                 onMouseUp={e => dropPiece(e)}
                 class="board"
                 ref={boardRef}>{boardArray}</div>
                 );
}
